package main

import (
	"fmt"
	"math/big"
)

func main() {
	num := big.NewInt(0)
	num.SetString("", 10)

	ten := big.NewInt(10)
	display := big.NewInt(0)

	digits := make([]int64, 1000)

	for ind := range digits {
		if ind == 999 {
			digits[0] = num.Int64()
		} else {
			num.DivMod(num, ten, display)
			digits[999-ind] = display.Int64()
		}
	}

	var check int64
	var max int64

	for j := 0; j < 987; j++ {
		check = digits[j] * digits[j+1] * digits[j+2] * digits[j+3] * digits[j+4] * digits[j+5] * digits[j+6] * digits[j+7] * digits[j+8] * digits[j+9] * digits[j+10] * digits[j+11] * digits[j+12]
		if check > max {
			max = check
		}
	}

	fmt.Println("Max:", max)
}
